data = DATA.read.lines

# Part 1

def xmas_count(y, x, data)
  seqs = (0..3).map do |n|
    [
      [y + n, x],
      [y - n, x],
      [y, x + n],
      [y, x - n],
      [y + n, x + n],
      [y + n, x - n],
      [y - n, x + n],
      [y - n, x - n]
    ]
  end

  seqs[0].zip(*seqs[1..]).count { _1.map { |(y, x)| ((0...data.size).cover?(y) && (0...data[y].size).cover?(x)) ? data[y][x] : '!' }.join("") == "XMAS" }
end

res1 = data.each_with_index.sum do |line, y|
  line.chomp.chars.each_with_index.sum { |char, x| xmas_count(y, x, data) }
end

puts "Part 1: #{res1}"

# Part 2

def is_a_in_mas_x?(y, x, data)
  seqs = [
    [ [y - 1, x - 1], [y, x], [y + 1, x + 1] ],
    [ [y + 1, x + 1], [y, x], [y - 1, x - 1] ],
    [ [y - 1, x + 1], [y, x], [y + 1, x - 1] ],
    [ [y + 1, x - 1], [y, x], [y - 1, x + 1] ],
  ]

  seqs.count { _1.map { |(y, x)| ((0...data.size).cover?(y) && (0...data[y].size).cover?(x)) ? data[y][x] : '!' }.join("") == "MAS" } == 2
end

res2 = data.each_with_index.sum do |line, y|
  line.chomp.chars.each_with_index.count { |char, x| is_a_in_mas_x?(y, x, data) }
end

puts "Part 2: #{res2}"

__END__
